public class L238ProductOfArrayExceptSelf {    public class Solution {        public int[] productExceptSelf(int[] nums) {            int n = nums.length;            int a[] = new int[n];            for (int i = 0; i < n; i++) {                a[i] = 1;            }            int left = 1, right = 1;            for (int i = 0; i < a.length; i++) {                a[i] *= left;                a[n - 1 - i] *= right;                left *= nums[i];                right *= nums[n - 1 - i];            }            return a;        }    }}