import java.util.Collections;import java.util.PriorityQueue;public class L239SlidingWindowMaximum {    public class Solution {        public int[] maxSlidingWindow(int[] nums, int k) {            int len = nums.length;            if (len == 0) {                int[] re = {};                return re;            }            PriorityQueue<Integer> queue = new PriorityQueue<Integer>(k, Collections.reverseOrder());            int[] re = new int[len + 1 - k];            int i = 0;            for (; i < k - 1; i++) {                queue.add(nums[i]);            }            int j = 0;            for (; i < len; i++) {                queue.add(nums[i]);                re[j] += queue.peek();                queue.remove(nums[j]);                j++;            }            return re;        }    }}