public class L240SearchA2DMatrixII {    public class Solution {        public boolean searchMatrix(int[][] matrix, int target) {            int m = matrix.length;            if (m == 0)                return false;            int n = matrix[0].length;            int i = 0, j = n - 1;            while (0 <= i && i < m && 0 <= j && j < n) {                if (matrix[i][j] == target) {                    return true;                } else if (matrix[i][j] < target) {                    i++;                } else {                    j--;                }            }            return false;        }    }}