import java.util.ArrayList;import java.util.List;public class L241DifferentWaysToAddParentheses {    public class Solution {        public List<Integer> diffWaysToCompute(String input) {            List<Integer> re = new ArrayList<Integer>();            if (input.length() == 0) {                re.add(0);                return re;            }            for (int i = 0; i < input.length(); ++i) {                if (input.charAt(i) == '+' || input.charAt(i) == '-' || input.charAt(i) == '*') {                    List<Integer> left = diffWaysToCompute(input.substring(0, i));                    List<Integer> right = diffWaysToCompute(input.substring(i + 1));                    for (int j = 0; j < left.size(); ++j) {                        for (int k = 0; k < right.size(); ++k) {                            if (input.charAt(i) == '+')                                re.add(left.get(j) + right.get(k));                            else if (input.charAt(i) == '-')                                re.add(left.get(j) - right.get(k));                            else                                re.add(left.get(j) * right.get(k));                        }                    }                }            }            if (re.isEmpty()) {                re.add(Integer.valueOf(input));            }            return re;        }    }}