public class L242ValidAnagram {    public class Solution {        public boolean isAnagram(String s, String t) {            int hash[] = new int[26];            if (s.length() != t.length())                return false;            for (int i = 0; i < s.length(); ++i) {                hash[s.charAt(i) - 'a']++;            }            for (int i = 0; i < t.length(); ++i) {                hash[t.charAt(i) - 'a']--;                if (hash[t.charAt(i) - 'a'] < 0)                    return false;            }            return true;        }    }}