public class L260SingleNumberIII {    public class Solution {        public int[] singleNumber(int[] nums) {            int[] re = new int[2];            int s = 0;            for (int i : nums) {                s ^= i;            }            int tmp = s;            int k = 0;            while (((tmp >> k) & 1) == 0) {                k++;            }            for (int i : nums) {                if (((i >> k) & 1) == 1) {                    tmp ^= i;                }            }            re[0] = tmp;            re[1] = tmp ^ s;            return re;        }    }}