public class L264UglyNumberII {    public class Solution {        public int nthUglyNumber(int n) {            int nums[] = new int[n];            nums[0] = 1;            int two = 0;            int three = 0;            int five = 0;            for (int i = 1; i < n; ++i) {                int min = Math.min(nums[two] * 2, Math.min(nums[three] * 3, nums[five] * 5));                nums[i] = min;                if (nums[two] * 2 == nums[i])                    ++two;                if (nums[three] * 3 == nums[i])                    ++three;                if (nums[five] * 5 == nums[i])                    ++five;            }            return nums[n - 1];        }    }}