public class L300LongestIncreasingSubsequence {    public class Solution {        public int lengthOfLIS(int[] nums) {            int dp[] = new int[nums.length];            int lis_len = 0;            for (int i : nums) {                int index = find(dp, lis_len, i);                dp[index] = i;                lis_len = Math.max(lis_len, index + 1);            }            return lis_len;        }        private int find(int[] arr, int len, int target) {            int start = 0, end = len - 1;            while (start <= end) {                int mid = ((end - start) >> 1) + start;                if (arr[mid] < target) {                    start = mid + 1;                } else {                    end = mid - 1;                }            }            return start;        }    }}